package day4;

public class SwitchEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		char op = '%';
		
		switch (op) {
		case '+':
			System.out.println("+ 연산자군요");
			break;
		case '-':
			System.out.println("- 연산자군요");
			break;
			
		default:
			System.out.println("사칙연산자가 아니군요");
			break;
		}
		

	}

}
